// Data validation for incoming webhook data
const data = $json;

// Required fields validation
const requiredFields = ['title', 'startDateTime'];
const missingFields = [];

for (const field of requiredFields) {
  if (!data[field]) {
    missingFields.push(field);
  }
}

if (missingFields.length > 0) {
  return [{
    json: {
      error: true,
      message: `Missing required fields: ${missingFields.join(', ')}`,
      statusCode: 400
    }
  }];
}

// Validate date format
if (!Date.parse(data.startDateTime)) {
  return [{
    json: {
      error: true,
      message: 'Invalid startDateTime format. Use ISO 8601 format.',
      statusCode: 400
    }
  }];
}

// Format and validate data
const validatedData = {
  title: data.title.trim(),
  description: data.description || '',
  startDateTime: new Date(data.startDateTime).toISOString(),
  endDateTime: data.endDateTime ? new Date(data.endDateTime).toISOString() : new Date(new Date(data.startDateTime).getTime() + 60 * 60 * 1000).toISOString(), // Default 1 hour duration
  attendees: Array.isArray(data.attendees) ? data.attendees : [],
  slackChannels: Array.isArray(data.slackChannels) ? data.slackChannels : ['#general'],
  reminderMinutes: data.reminderMinutes || 15,
  timezone: data.timezone || 'UTC'
};

return [{
  json: {
    ...validatedData,
    valid: true
  }
}];

// Calculate reminder schedule based on event start time
const eventStart = new Date($json.eventStart);
const reminderMinutes = $json.originalData.reminderMinutes;
const reminderTime = new Date(eventStart.getTime() - (reminderMinutes * 60 * 1000));

// Check if reminder time is in the future
const now = new Date();
if (reminderTime <= now) {
  return [{
    json: {
      skipReminder: true,
      message: 'Event is too soon for reminder scheduling'
    }
  }];
}

return [{
  json: {
    ...($json),
    reminderTime: reminderTime.toISOString(),
    skipReminder: false,
    cronExpression: `${reminderTime.getMinutes()} ${reminderTime.getHours()} ${reminderTime.getDate()} ${reminderTime.getMonth() + 1} *`
  }
}];

// Format reminder message for Slack
const eventData = $json.eventData || $json;
const eventTitle = eventData.eventTitle || eventData.originalData.title;
const eventStart = new Date(eventData.eventStart || eventData.originalData.startDateTime);
const eventLink = eventData.eventLink || '#';
const reminderMinutes = eventData.originalData.reminderMinutes;

// Format time
